CREATE TABLE IF NOT EXISTS customers (id SERIAL PRIMARY KEY, customer_code VARCHAR(20) UNIQUE, name VARCHAR(200), active BOOLEAN DEFAULT true); CREATE TABLE IF NOT EXISTS parts (id SERIAL PRIMARY KEY, part_number VARCHAR(50) UNIQUE, description TEXT, active BOOLEAN DEFAULT true); CREATE TABLE IF NOT EXISTS transactions (id SERIAL PRIMARY KEY, lot_id INTEGER, quantity INTEGER, transaction_type VARCHAR(20)); CREATE TABLE IF NOT EXISTS storage_locations (id SERIAL PRIMARY KEY, location_code VARCHAR(20) UNIQUE, description TEXT, active BOOLEAN DEFAULT true); CREATE TABLE IF NOT EXISTS material_indices (id SERIAL PRIMARY KEY, name VARCHAR(200), active BOOLEAN DEFAULT true);
